import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import './LanguageSelect.css';
import bgImage from './tokyoguesthouse2.png'; // Use your preferred image

const LanguageSelect = () => {
  const navigate = useNavigate();
  const { i18n } = useTranslation();

  const handleEnglish = () => {
    i18n.changeLanguage('en');
    navigate('/Home');
  };

  const handleJapanese = () => {
    i18n.changeLanguage('ja');
    // Optionally, navigate to a Japanese home page or just stay
  };

  return (
    <div className="language-select-bg" style={{
      backgroundImage: `url(${bgImage})`,
      minHeight: '100vh',
      width: '100vw',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center'
    }}>
        <div className="welcomemessage">Tokyo Guest House Oji Music Lounge</div>
        <button className="lang-btn" onClick={handleEnglish}>English</button>
        <button className="lang-btn" onClick={handleJapanese}>日本語</button>
    </div>
  );
};

export default LanguageSelect;
