  // Helper: Get room array by option
  function getRoomArray(roomOption) {
    switch (roomOption) {
      case 'Dormitory': return dormitoryRooms;
      case 'Double Room Shared Toilet & Shower': return doubleSharedRooms;
      case 'Double Room Private Toilet & Bath': return doubleRoomPrivate;
      case 'Japanese Twin Room': return japaneseTwinRooms;
      case '4 Bed Room': return fourbedRooms;
      default: return [];
    }
  }

// Helper: Check if a room type is available for a date range, allowing touching reservations
function isRoomTypeAvailable(roomOption, startDate, endDate) {
  if (!reservationData || !startDate || !endDate) return true;
  const roomArray = getRoomArray(roomOption);
  // For each room, check if it is available for the whole range, allowing touching (start == prev end, end == next start)
  return roomArray.some(room => {
    // Get all reservations for this room
    const reservationsForRoom = reservationData.filter(r => r['Room Option'] === roomOption && r['Room Number'] === room);
    // Check for overlap (not allowing touching)
    const hasConflict = reservationsForRoom.some(r => {
      const rStart = new Date(r['Start Date']);
      const rEnd = new Date(r['End Date']);
      // Allow touching: startDate == rEnd or endDate == rStart
      return (startDate < rEnd && endDate > rStart);
    });
    return !hasConflict;
  });
}

// Helper: For a people count, is there any room type available for the selected date range?
