import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LanguageSelect.css';
import bgImage from './tokyoguesthouse2.png';

const LanguageSelect = () => {
  const navigate = useNavigate();
  const [translating, setTranslating] = useState(false);
  const [widgetReady, setWidgetReady] = useState(false);
  const [pendingLang, setPendingLang] = useState(null);

  // Inject Google Translate script and hide the default widget
  useEffect(() => {
    if (!window.googleTranslateElementInit) {
      window.googleTranslateElementInit = function () {
        new window.google.translate.TranslateElement(
          {
            pageLanguage: 'en',
            includedLanguages: 'en,ja',
            layout: window.google.translate.TranslateElement.InlineLayout.SIMPLE,
            autoDisplay: false
          },
          'google_translate_element'
        );
      };
      const script = document.createElement('script');
      script.src = '//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit';
      script.async = true;
      document.body.appendChild(script);
    }
    // Hide the widget visually
    const style = document.createElement('style');
    style.innerHTML = `
      #google_translate_element { display: none !important; }
      .goog-te-banner-frame.skiptranslate, .goog-te-gadget-icon { display: none !important; }
      body { top: 0px !important; }
    `;
    document.head.appendChild(style);
  }, []);

  // Wait for the Google Translate widget to be ready
  useEffect(() => {
    const interval = setInterval(() => {
      if (document.querySelector('.goog-te-combo')) {
        setWidgetReady(true);
        clearInterval(interval);
      }
    }, 200);
    return () => clearInterval(interval);
  }, []);

  // Helper to trigger Google Translate language change
  const setLanguage = (lang) => {
    const select = document.querySelector('.goog-te-combo');
    if (select) {
      select.value = lang;
      select.dispatchEvent(new Event('change'));
    }
  };

  // Observe translation completion and redirect
  useEffect(() => {
    if (!pendingLang) return;
    setTranslating(true);
    setLanguage(pendingLang);
    // Observe DOM changes to detect when translation is applied
    const observer = new MutationObserver(() => {
      const html = document.documentElement;
      if (
        (pendingLang === 'ja' && html.lang === 'ja') ||
        (pendingLang === 'en' && html.lang === 'en')
      ) {
        observer.disconnect();
        setTranslating(false);
        setPendingLang(null);
        navigate('/Home');
      }
    });
    observer.observe(document.documentElement, { attributes: true, attributeFilter: ['lang'] });
    // Fallback: If translation takes too long, navigate anyway after 5 seconds
    const timeout = setTimeout(() => {
      observer.disconnect();
      setTranslating(false);
      setPendingLang(null);
      navigate('/Home');
    }, 5000);
    return () => {
      observer.disconnect();
      clearTimeout(timeout);
    };
    // eslint-disable-next-line
  }, [pendingLang]);

  const handleEnglish = () => {
    setPendingLang('en');
  };

  const handleJapanese = () => {
    setPendingLang('ja');
  };

  return (
    <div
      className="language-select-bg"
      style={{
        backgroundImage: `url(${bgImage})`,
        minHeight: '100vh',
        width: '100vw',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center'
      }}
    >
      {/* Google Translate element (hidden) */}
      <div id="google_translate_element" style={{ display: 'none' }}></div>
      <div className="welcomemessage">Tokyo Guest House Oji Music Lounge</div>
      <br />
      <br />
      {translating && (
        <div style={{
          fontSize: '2rem',
          color: 'white',
          background: 'rgba(0,0,0,0.7)',
          padding: '1rem 2rem',
          borderRadius: '10px',
          marginBottom: '2rem'
        }}>
          Translating...
        </div>
      )}
      <button className="lang-btn" onClick={handleEnglish} disabled={!widgetReady || translating}>English</button>
      <button className="lang-btn" onClick={handleJapanese} disabled={!widgetReady || translating}>日本語</button>
    </div>
  );
};

export default LanguageSelect;
