import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LanguageSelect.css';
import bgImage from './tokyoguesthouse2.png';

const LanguageSelect = () => {
  const navigate = useNavigate();
  const [translating, setTranslating] = useState(false);

  // Inject Google Translate script and hide the default widget
  useEffect(() => {
    // Only add script if it doesn't exist
    if (!window.googleTranslateElementInit) {
      window.googleTranslateElementInit = function () {
        new window.google.translate.TranslateElement(
          {
            pageLanguage: 'en',
            includedLanguages: 'en,ja',
            layout: window.google.translate.TranslateElement.InlineLayout.SIMPLE,
            autoDisplay: false
          },
          'google_translate_element'
        );
      };
      const script = document.createElement('script');
      script.src = '//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit';
      script.async = true;
      document.body.appendChild(script);
    }
    // Hide the widget visually
    const style = document.createElement('style');
    style.innerHTML = `
      #google_translate_element { display: none !important; }
      .goog-te-banner-frame.skiptranslate, .goog-te-gadget-icon { display: none !important; }
      body { top: 0px !important; }
    `;
    document.head.appendChild(style);
  }, []);

  // Helper to trigger Google Translate language change
  const setLanguage = (lang) => {
    if (window.google && window.google.translate) {
      const select = document.querySelector('.goog-te-combo');
      if (select) {
        select.value = lang;
        select.dispatchEvent(new Event('change'));
      }
    }
  };

  const handleEnglish = () => {
    setLanguage('en');
    navigate('/Home');
  };

  const handleJapanese = () => {
  setTranslating(true);
  setLanguage('ja');

  // Observe DOM changes to detect when translation is applied
  const observer = new MutationObserver(() => {
    // Check for the presence of the Google Translate banner frame or translated text
    const banner = document.querySelector('.goog-te-banner-frame');
    // You can also check for a change in a specific element's textContent if you want
    if (banner || document.body.classList.contains('translated-ltr')) {
      observer.disconnect();
      setTranslating(false);
      navigate('/Home');
    }
  });

  // Start observing the body for changes
  observer.observe(document.body, { childList: true, subtree: true });

    // Fallback: If translation takes too long, navigate anyway after 3 seconds
    setTimeout(() => {
        observer.disconnect();
        setTranslating(false);
        navigate('/Home');
    }, 20000);
    };

  return (
    <div
      className="language-select-bg"
      style={{
        backgroundImage: `url(${bgImage})`,
        minHeight: '100vh',
        width: '100vw',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center'
      }}
    >
      {/* Google Translate element (hidden) */}
      <div id="google_translate_element" style={{ display: 'none' }}></div>
      <div className="welcomemessage">Tokyo Guest House Oji Music Lounge</div>
      <br />
      <br />
      {translating && (
        <div style={{
            fontSize: '2rem',
            color: 'white',
            background: 'rgba(0,0,0,0.7)',
            padding: '1rem 2rem',
            borderRadius: '10px',
            marginBottom: '2rem'
        }}>
            Translating...
        </div>
        )}
        <button className="lang-btn" onClick={handleEnglish}>English</button>
        <button className="lang-btn" onClick={handleJapanese}>日本語</button>
    </div>
  );
};

export default LanguageSelect;
