import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LanguageSelect.css';
import bgImage from './tokyoguesthouse2.png';

const LanguageSelect = () => {
  const navigate = useNavigate();

  return (
    <div
      className="language-select-bg"
      style={{
        backgroundImage: `url(${bgImage})`,
        minHeight: '100vh',
        width: '100vw',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center'
      }}
    >
      {/* Google Translate element (hidden) */}
      <div id="google_translate_element" style={{ display: 'none' }}></div>
      <div className="welcomemessage">Tokyo Guest House Oji Music Lounge</div>
      <br />
      <br />
      {translating && (
        <div style={{
          fontSize: '2rem',
          color: 'white',
          background: 'rgba(0,0,0,0.7)',
          padding: '1rem 2rem',
          borderRadius: '10px',
          marginBottom: '2rem'
        }}>
          Translating...
        </div>
      )}
      <button className="lang-btn" onClick={handleEnglish} disabled={!widgetReady || translating}>English</button>
      <button className="lang-btn" onClick={handleJapanese} disabled={!widgetReady || translating}>日本語</button>
    </div>
  );
};

export default LanguageSelect;
